<span class="line"><span style="color:#A6ACCD;">         </span><span style="color:#B2CCD6;">background-image</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">url</span><span style="color:#89DDFF;">(/</span><span style="color:#A6ACCD;font-style:italic;">logo.gif</span><span style="color:#89DDFF;">);</span></span>
